import { useState } from "react";

export default function AdminPage() {
  const [students, setStudents] = useState([]);
  const [teachers, setTeachers] = useState([]);
  const [posts, setPosts] = useState([]);
  const [events, setEvents] = useState([]);
  const [alert, setAlert] = useState("");

  const handleAddStudent = () => {
    // Logic to add a new student
    setAlert("New student account created.");
  };

  const handleAddTeacher = () => {
    // Logic to add a new teacher
    setAlert("New teacher account created.");
  };

  const handleEditInfo = (type, id) => {
    // Logic to edit student/teacher information
    setAlert(`${type} information updated.`);
  };

  const handleCreatePost = () => {
    // Logic to create a new post
    setAlert("New post created.");
  };

  const handleCreateEvent = () => {
    // Logic to create a new event
    setAlert("New event created.");
  };

  return (
    <div className="p-8 bg-gray-100 min-h-screen">
      {alert && (
        <div className="mb-4 p-2 bg-green-500 text-white text-center rounded">
          {alert}
        </div>
      )}
      <h1 className="text-3xl font-bold mb-6 text-center">Admin Dashboard</h1>

      <section className="mb-8">
        <h2 className="text-2xl font-semibold mb-4">Create Accounts</h2>
        <div className="flex gap-4">
          <button
            onClick={handleAddStudent}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          >
            Add Student
          </button>
          <button
            onClick={handleAddTeacher}
            className="bg-purple-500 text-white px-4 py-2 rounded hover:bg-purple-600"
          >
            Add Teacher
          </button>
        </div>
      </section>

      <section className="mb-8">
        <h2 className="text-2xl font-semibold mb-4">Edit User Information</h2>
        <p>Feature to edit user information and reset passwords here.</p>
        {/* Add form or modal to handle editing logic */}
      </section>

      <section className="mb-8">
        <h2 className="text-2xl font-semibold mb-4">Manage Posts</h2>
        <button
          onClick={handleCreatePost}
          className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
        >
          Create Post
        </button>
      </section>

      <section>
        <h2 className="text-2xl font-semibold mb-4">Manage Events</h2>
        <button
          onClick={handleCreateEvent}
          className="bg-yellow-500 text-white px-4 py-2 rounded hover:bg-yellow-600"
        >
          Create Event
        </button>
      </section>
    </div>
  );
}
