import { useState } from "react";

export default function AdminPage() {
  const [selectedFeature, setSelectedFeature] = useState("");
  const [alert, setAlert] = useState("");

  const handleAddStudent = () => {
    setAlert("New student account created.");
  };

  const handleAddTeacher = () => {
    setAlert("New teacher account created.");
  };

  const handleEditInfo = (type) => {
    setAlert(`${type} information updated.`);
  };

  const handleCreatePost = () => {
    setAlert("New post created.");
  };

  const handleCreateEvent = () => {
    setAlert("New event created.");
  };

  const renderContent = () => {
    switch (selectedFeature) {
      case "addStudent":
        return <div>Student account creation logic here.</div>;
      case "addTeacher":
        return <div>Teacher account creation logic here.</div>;
      case "editInfo":
        return <div>Edit user information form here.</div>;
      case "createPost":
        return <div>Post creation logic here.</div>;
      case "createEvent":
        return <div>Event creation logic here.</div>;
      default:
        return <div>Select a feature to manage.</div>;
    }
  };

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Left Sidebar */}
      <div className="w-1/4 bg-gray-200 p-4">
        <h2 className="text-xl font-semibold mb-4">Admin Features</h2>
        <ul className="space-y-2">
          <li>
            <button
              onClick={() => setSelectedFeature("addStudent")}
              className="w-full text-left bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
            >
              Add Student
            </button>
          </li>
          <li>
            <button
              onClick={() => setSelectedFeature("addTeacher")}
              className="w-full text-left bg-purple-500 text-white px-4 py-2 rounded hover:bg-purple-600"
            >
              Add Teacher
            </button>
          </li>
          <li>
            <button
              onClick={() => setSelectedFeature("editInfo")}
              className="w-full text-left bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600"
            >
              Edit User Info
            </button>
          </li>
          <li>
            <button
              onClick={() => setSelectedFeature("createPost")}
              className="w-full text-left bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
            >
              Create Post
            </button>
          </li>
          <li>
            <button
              onClick={() => setSelectedFeature("createEvent")}
              className="w-full text-left bg-yellow-500 text-white px-4 py-2 rounded hover:bg-yellow-600"
            >
              Create Event
            </button>
          </li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="w-3/4 p-8">
        {alert && (
          <div className="mb-4 p-2 bg-green-500 text-white text-center rounded">
            {alert}
          </div>
        )}
        <h1 className="text-3xl font-bold mb-6 text-center">Admin Dashboard</h1>
        {renderContent()}
      </div>
    </div>
  );
}
